mapper.select:
select *
from deal
where deal_type = 'PURCHASE'
  and status = 'BIDDING'
  and item_id = #{param1}
  and size = #{param2}
  and price = #{param3}
  limit 1

select *
from deal
where deal_type = 'SALE'
  and status = 'BIDDING'
  and item_id = #{param1}
  and size = #{param2}
  and price = #{param3}
  limit 1

select price as price, size as size, trading_day as tradingDay from deal where item_id = #{param1}
and size = #{param2}
and status = 'COMPLETION' order by trading_day DESC

select price as price,
size as size,
trading_day as tradingDay
from deal
where item_id = #{param1}
and status = 'COMPLETION'

  and trading_day >= #{param2}


  and size = #{param3}

order by trading_day DESC

select price as price, size as size, count(*) as count from deal where item_id = #{param1} and status = 'BIDDING' and deal_type = #{param3}
and size = #{param2}
group by size, price
order by price DESC
order by price

select status as status,
       count(*) as count
from deal
where user_id = #{param1}
  and deal_type = #{param2}
group by status
